#define RANDOM_NUMBERS 100000

typedef struct Node {
    char name[50];
    int income;
    struct Node* next;
} Node;

Node* head = NULL;

void insert(char name[], int income) {
    Node* new_node = (Node*)malloc(sizeof(Node));
    strcpy(new_node->name, name);
    new_node->income = income;
    new_node->next = NULL;
    
    if (head == NULL) {
        head = new_node;
        return;
    }
    
    Node* curr = head;
    Node* prev = NULL;
    while (curr != NULL && curr->income < income) {
        prev = curr;
        curr = curr->next;
    }
    
    if (prev == NULL) {
        new_node->next = head;
        head = new_node;
    } else {
        prev->next = new_node;
        new_node->next = curr;
    }
}

