void range_search(int value, int flag) {
    Node* curr = head;
    while (curr != NULL) {
        if (flag == 0 && curr->income < value) {
            // printing will take a long time
            //printf("%d\n", curr->income);
        } else if (flag == 1 && curr->income >= value) {
            //printf("%d\n", curr->income);
        } else if (flag == 0 && curr->income >= value) {
            break;  // since the values are always sorted, when a value is greater than or equal, we can stop
        }
        curr = curr->next;
    }
}

void range_search2(int start, int end) {
    Node* curr = head;
    while (curr != NULL) {
        if (curr->income >= start && curr->income < end) {
            //printf("%d\n", curr->income);
        } else if (curr->income >= end) break;
        curr = curr->next;
    }
}

